import { View, Text, StyleSheet, ScrollView, TouchableOpacity } from "react-native"
import { SafeAreaView } from "react-native-safe-area-context"
import { useNavigation } from "@react-navigation/native"
import { ArrowRight } from "react-native-feather"

import PineappleUploader from "../components/PineappleUploader"
import { theme } from "../theme"

export default function HomeScreen() {
  const navigation = useNavigation()

  return (
    <SafeAreaView style={styles.container} edges={["bottom"]}>
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.header}>
          <Text style={styles.title}>Predict Pineapple Sweetness</Text>
          <Text style={styles.titleAccent}>Using AI</Text>
          <Text style={styles.subtitle}>
            Our advanced machine learning algorithm analyzes pineapple images to predict sweetness levels. Simply upload
            a photo of your pineapple and get instant results.
          </Text>
        </View>

        <View style={styles.uploaderContainer}>
          <PineappleUploader />
        </View>

        <View style={styles.navigationButtons}>
          <TouchableOpacity style={styles.navButton} onPress={() => navigation.navigate("HowItWorks")}>
            <Text style={styles.navButtonText}>How It Works</Text>
            <ArrowRight stroke={theme.colors.yellow[600]} width={16} height={16} />
          </TouchableOpacity>

          <TouchableOpacity style={styles.navButton} onPress={() => navigation.navigate("About")}>
            <Text style={styles.navButtonText}>About The Project</Text>
            <ArrowRight stroke={theme.colors.yellow[600]} width={16} height={16} />
          </TouchableOpacity>
        </View>
      </ScrollView>
    </SafeAreaView>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: theme.colors.background,
  },
  scrollContent: {
    padding: 20,
  },
  header: {
    alignItems: "center",
    marginBottom: 24,
  },
  title: {
    fontSize: 28,
    fontWeight: "bold",
    color: theme.colors.text,
    textAlign: "center",
  },
  titleAccent: {
    fontSize: 28,
    fontWeight: "bold",
    color: theme.colors.yellow[500],
    textAlign: "center",
    marginBottom: 12,
  },
  subtitle: {
    fontSize: 16,
    color: theme.colors.textMuted,
    textAlign: "center",
    marginHorizontal: 20,
  },
  uploaderContainer: {
    marginVertical: 24,
  },
  navigationButtons: {
    marginTop: 12,
  },
  navButton: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    padding: 16,
    backgroundColor: theme.colors.card,
    borderRadius: 8,
    marginBottom: 12,
    ...theme.shadows.sm,
  },
  navButtonText: {
    fontSize: 16,
    fontWeight: "500",
    color: theme.colors.text,
  },
})
