import { View, Text, StyleSheet, Image, TouchableOpacity, ActivityIndicator } from "react-native"
import { SafeAreaView } from "react-native-safe-area-context"
import { useAuth } from "../context/AuthContext"
import { theme } from "../theme"

export default function LoginScreen() {
  const { signInWithGoogle, isLoading } = useAuth()

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.content}>
        <View style={styles.logoContainer}>
          <Text style={styles.emoji}>üçç</Text>
          <Text style={styles.appName}>Pineapple Sweetness</Text>
          <Text style={styles.tagline}>Predict sweetness with AI</Text>
        </View>

        <View style={styles.infoContainer}>
          <Text style={styles.infoTitle}>Welcome!</Text>
          <Text style={styles.infoText}>
            Sign in to save your pineapple analysis history and access personalized recommendations.
          </Text>
        </View>

        {isLoading ? (
          <ActivityIndicator size="large" color={theme.colors.yellow[500]} />
        ) : (
          <TouchableOpacity style={styles.googleButton} onPress={signInWithGoogle} disabled={isLoading}>
            <Image
              source={{ uri: "https://developers.google.com/identity/images/g-logo.png" }}
              style={styles.googleIcon}
            />
            <Text style={styles.googleButtonText}>Sign in with Google</Text>
          </TouchableOpacity>
        )}

        <View style={styles.termsContainer}>
          <Text style={styles.termsText}>
            By signing in, you agree to our <Text style={styles.termsLink}>Terms of Service</Text> and{" "}
            <Text style={styles.termsLink}>Privacy Policy</Text>
          </Text>
        </View>
      </View>
    </SafeAreaView>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: theme.colors.background,
  },
  content: {
    flex: 1,
    padding: 24,
    justifyContent: "center",
    alignItems: "center",
  },
  logoContainer: {
    alignItems: "center",
    marginBottom: 48,
  },
  emoji: {
    fontSize: 64,
    marginBottom: 16,
  },
  appName: {
    fontSize: 28,
    fontWeight: "bold",
    color: theme.colors.text,
  },
  tagline: {
    fontSize: 16,
    color: theme.colors.textMuted,
    marginTop: 8,
  },
  infoContainer: {
    marginBottom: 32,
    alignItems: "center",
  },
  infoTitle: {
    fontSize: 24,
    fontWeight: "bold",
    color: theme.colors.text,
    marginBottom: 12,
  },
  infoText: {
    fontSize: 16,
    color: theme.colors.textMuted,
    textAlign: "center",
    lineHeight: 24,
  },
  googleButton: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "white",
    borderRadius: 4,
    paddingVertical: 12,
    paddingHorizontal: 24,
    marginBottom: 16,
    borderWidth: 1,
    borderColor: "#ddd",
    ...theme.shadows.sm,
  },
  googleIcon: {
    width: 24,
    height: 24,
    marginRight: 12,
  },
  googleButtonText: {
    fontSize: 16,
    fontWeight: "500",
    color: "#444",
  },
  termsContainer: {
    marginTop: 24,
    paddingHorizontal: 24,
  },
  termsText: {
    fontSize: 14,
    color: theme.colors.textMuted,
    textAlign: "center",
  },
  termsLink: {
    color: theme.colors.yellow[600],
  },
})
