"use client"

import { useState } from "react"
import { View, Text, StyleSheet, Image, TouchableOpacity, ActivityIndicator } from "react-native"
import * as ImagePicker from "expo-image-picker"
import { Camera, Upload } from "react-native-feather"

import { theme } from "../theme"

export default function PineappleUploader() {
  const [image, setImage] = useState(null)
  const [isAnalyzing, setIsAnalyzing] = useState(false)
  const [result, setResult] = useState(null)

  const pickImage = async () => {
    // Request permission
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync()

    if (status !== "granted") {
      alert("Sorry, we need camera roll permissions to make this work!")
      return
    }

    // Launch image picker
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 0.8,
    })

    if (!result.canceled) {
      setImage(result.assets[0].uri)
      setResult(null)
    }
  }

  const takePhoto = async () => {
    // Request permission
    const { status } = await ImagePicker.requestCameraPermissionsAsync()

    if (status !== "granted") {
      alert("Sorry, we need camera permissions to make this work!")
      return
    }

    // Launch camera
    const result = await ImagePicker.launchCameraAsync({
      allowsEditing: true,
      aspect: [1, 1],
      quality: 0.8,
    })

    if (!result.canceled) {
      setImage(result.assets[0].uri)
      setResult(null)
    }
  }

  const analyzeImage = () => {
    setIsAnalyzing(true)
    // Simulate API call to ML model
    setTimeout(() => {
      setResult({
        sweetness: 8.7,
        confidence: 92,
        category: "Very Sweet",
        characteristics: ["Optimal yellow-to-green ratio", "Healthy frond appearance", 'Medium-sized "eyes"'],
      })
      setIsAnalyzing(false)
    }, 2500)
  }

  const resetUploader = () => {
    setImage(null)
    setResult(null)
  }

  if (!image) {
    return (
      <View style={styles.container}>
        <View style={styles.header}>
          <View style={styles.iconContainer}>
            <Camera stroke={theme.colors.yellow[600]} width={24} height={24} />
          </View>
          <Text style={styles.title}>Upload Pineapple Image</Text>
          <Text style={styles.subtitle}>Take a clear photo of the whole pineapple for the best results</Text>
        </View>

        <View style={styles.uploadOptions}>
          <TouchableOpacity style={styles.uploadButton} onPress={takePhoto}>
            <Camera stroke={theme.colors.text} width={24} height={24} />
            <Text style={styles.uploadButtonText}>Take Photo</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.uploadButton} onPress={pickImage}>
            <Upload stroke={theme.colors.text} width={24} height={24} />
            <Text style={styles.uploadButtonText}>Choose from Gallery</Text>
          </TouchableOpacity>
        </View>

        <Text style={styles.terms}>By uploading an image, you agree to our Terms of Service</Text>
      </View>
    )
  }

  return (
    <View style={styles.container}>
      <View style={styles.imageContainer}>
        <Image source={{ uri: image }} style={styles.image} />

        <TouchableOpacity style={styles.resetButton} onPress={resetUploader}>
          <Text style={styles.resetButtonText}>Upload a different image</Text>
        </TouchableOpacity>
      </View>

      <View style={styles.resultContainer}>
        {isAnalyzing ? (
          <View style={styles.analyzing}>
            <ActivityIndicator size="large" color={theme.colors.yellow[500]} />
            <Text style={styles.analyzingText}>Analyzing your pineapple...</Text>
            <Text style={styles.analyzingSubtext}>Our AI is examining visual characteristics</Text>
          </View>
        ) : result ? (
          <View style={styles.results}>
            <View style={styles.resultHeader}>
              <Text style={styles.resultCategory}>{result.category}</Text>
              <Text style={styles.resultScore}>Sweetness score: {result.sweetness}/10</Text>

              <View style={styles.confidenceContainer}>
                <Text style={styles.confidenceLabel}>Confidence:</Text>
                <View style={styles.confidenceBar}>
                  <View style={[styles.confidenceFill, { width: `${result.confidence}%` }]} />
                </View>
                <Text style={styles.confidenceValue}>{result.confidence}%</Text>
              </View>
            </View>

            <View style={styles.characteristicsContainer}>
              <Text style={styles.characteristicsTitle}>Characteristics Detected:</Text>

              {result.characteristics.map((characteristic, index) => (
                <View key={index} style={styles.characteristic}>
                  <View
                    style={[
                      styles.characteristicDot,
                      { backgroundColor: index < 2 ? theme.colors.green[500] : theme.colors.yellow[500] },
                    ]}
                  />
                  <Text style={styles.characteristicText}>{characteristic}</Text>
                </View>
              ))}
            </View>
          </View>
        ) : (
          <View style={styles.readyToAnalyze}>
            <Text style={styles.readyTitle}>Ready to Analyze</Text>
            <Text style={styles.readySubtext}>
              Click the button below to analyze your pineapple and predict its sweetness
            </Text>

            <TouchableOpacity style={styles.analyzeButton} onPress={analyzeImage}>
              <Text style={styles.analyzeButtonText}>Analyze Pineapple</Text>
            </TouchableOpacity>
          </View>
        )}
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: theme.colors.card,
    borderRadius: 12,
    overflow: "hidden",
    ...theme.shadows.md,
  },
  header: {
    alignItems: "center",
    padding: 20,
  },
  iconContainer: {
    width: 48,
    height: 48,
    borderRadius: 24,
    backgroundColor: theme.colors.yellow[100],
    alignItems: "center",
    justifyContent: "center",
    marginBottom: 12,
  },
  title: {
    fontSize: 18,
    fontWeight: "bold",
    color: theme.colors.text,
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 14,
    color: theme.colors.textMuted,
    textAlign: "center",
  },
  uploadOptions: {
    padding: 16,
  },
  uploadButton: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: theme.colors.muted,
    borderRadius: 8,
    padding: 16,
    marginBottom: 12,
  },
  uploadButtonText: {
    marginLeft: 8,
    fontSize: 16,
    fontWeight: "500",
    color: theme.colors.text,
  },
  terms: {
    fontSize: 12,
    color: theme.colors.textMuted,
    textAlign: "center",
    marginTop: 8,
    marginBottom: 16,
    paddingHorizontal: 16,
  },
  imageContainer: {
    alignItems: "center",
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: theme.colors.border,
  },
  image: {
    width: 200,
    height: 200,
    borderRadius: 8,
  },
  resetButton: {
    marginTop: 12,
    padding: 8,
  },
  resetButtonText: {
    fontSize: 14,
    color: theme.colors.primary,
  },
  resultContainer: {
    padding: 16,
  },
  analyzing: {
    alignItems: "center",
    padding: 20,
  },
  analyzingText: {
    fontSize: 16,
    fontWeight: "600",
    color: theme.colors.text,
    marginTop: 12,
  },
  analyzingSubtext: {
    fontSize: 14,
    color: theme.colors.textMuted,
    marginTop: 4,
  },
  readyToAnalyze: {
    alignItems: "center",
    padding: 16,
  },
  readyTitle: {
    fontSize: 18,
    fontWeight: "600",
    color: theme.colors.text,
    marginBottom: 8,
  },
  readySubtext: {
    fontSize: 14,
    color: theme.colors.textMuted,
    textAlign: "center",
    marginBottom: 16,
  },
  analyzeButton: {
    backgroundColor: theme.colors.yellow[500],
    borderRadius: 8,
    paddingVertical: 12,
    paddingHorizontal: 24,
    ...theme.shadows.sm,
  },
  analyzeButtonText: {
    fontSize: 16,
    fontWeight: "600",
    color: "white",
  },
  results: {
    padding: 8,
  },
  resultHeader: {
    backgroundColor: theme.colors.yellow[50],
    borderRadius: 8,
    padding: 16,
    alignItems: "center",
    marginBottom: 16,
  },
  resultCategory: {
    fontSize: 22,
    fontWeight: "bold",
